import React, { Component } from 'react';
import axios from 'axios';
import './CrudTenis.css';
import Main from '../template/Main';

const title = "Cadastro de Tenis";

const urlAPI = "http://localhost:5176/api/tenis";
const initialState = {
    tenis: {id: 0, cod: '', nome:'', tamanho: 0},
    lista: []
}

export default class CrudTenis extends Component {

    state = {...initialState}

    componentDidMount(){
        axios(urlAPI).then(resp =>{
           this.setState({lista:resp.data})
        })
    }

    limpar(){
        this.setState({tenis: initialState.tenis});
    }

    salvar(){
        const tenis = this.state.tenis;
        tebis.cod = Number(tenis.cod);
        const metodo = 'post';

        axios[metodo](urlAPI, tenis)
            .then(resp => {
                const lista = this.get
            })
    }

    renderTable() {
        return (
            <div className="listagem">
                <table className="listaTenis" id="tblListaTenis">
                    <thead>
                        <tr className="cabecTabela">
                            <th className="tabTituloCod">Codigo</th>
                            <th className="tabTituloNome">Nome do Sneaker</th>
                            <th className="tabTituloTamanho">Tamanho</th>
                        </tr>
                    </thead>

                    <tbody>
                        {this.state.lista.map(
                            (tenis) =>
                            <tr key={tenis.id}>
                                <td>{tenis.cod}</td>
                                <td>{tenis.nome}</td>
                                <td>{tenis.tamanho}</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        )
    }

    render() {
        return (
            <Main title={title}>
                {this.renderTable()}
            </Main>
        )
    }
}
