import React, {Component} from "react";
import axios from 'axios';
import './CrudTenis.css';
import Main from "../template/Main";

const title = "Cadastro de Alunos";

const urlAPI = 'http://localhost:5014/api/tenis';
const urlCurso = "http://localhost:5014/api/curso";

const initialState = {
    aluno: { id: 0, ra: '', nome: '', codCurso: 0},
    lista: [], listaCursos: []
}

export default class CrudAluno extends Component {
    state = { ...initialState}

    componentDidMount() {
        axios(urlAPI).then(resp => {
            this.setState({ lista: resp.data })
        })

        axios(urlCurso).then(resp => {
            this.setState({ listaCursos: resp.data })
        })
    }

    limpar() {
        this.setState({ aluno: initialState.aluno });
    }

    salvar() {
        const aluno = this.state.aluno;
        aluno.codCurso = Number(aluno.codCurso);

        const metodo = aluno.id ? 'put' : 'post';
        const url = aluno.id ? `${urlAPI}/${aluno.id}` : urlAPI;

        axios[metodo](url, aluno)
            .then(resp => {
                const lista = this.getListaAtualizada(resp.data)
                this.setState({ aluno: initialState.aluno, lista })
            })
    }

    getListaAtualizada(aluno, add = true) {
        const lista = this.state.lista.filter(a => a.id !== aluno.id);
        if (add) lista.unshift(aluno);
        return lista;
    }

    atualizaCampo(evento) {
        const aluno = { ...this.state.aluno};
        aluno[evento.target.name] = evento.target.value;
        this.setState({aluno});
    }

    carregar(aluno) {
        this.setState({ aluno })
        const url = urlAPI + "/" + aluno.id;
        if (window.confirm("Confirma edição do aluno: " + aluno.ra)) {
            console.log("entrou no confirm");
            axios['PUT'](url, aluno).then(resp => {
                const lista = this.getListaAtualizada(aluno, false);
                this.setState({ aluno: initialState.aluno, lista });
            });
        }
        this.atualizaCampo(aluno);
    }

    remover(aluno) {
        const url = urlAPI + "/" + aluno.id;
        if (window.confirm("Confirma remoção do aluno: " + aluno.ra)) {
            console.log("entrou no confirme da tela")

            axios['delete'](url, aluno)
                .then(resp => {
                    const lista = this.getListaAtualizada(aluno, false)
                    this.setState({ aluno: initialState.aluno, lista })
                })
        }
    }

    renderForm() {
        return (
            <div className="inclui-container">

                <label> RA: </label>
                <input
                    type="text"
                    id="ra"
                    placeholder="RA do aluno"
                    className="form-input"
                    name="ra"
                    value={this.state.aluno.ra}
                    onChange={ e => this.atualizaCampo(e)}
                />

                <label> Nome: </label>
                <input
                    type="text"
                    id="nome"
                    placeholder="Nome do aluno"
                    className="form-input"
                    name="nome"
                    value={this.state.aluno.nome}
                    onChange={ e => this.atualizaCampo(e)}
                />

                <label> Código do Curso: </label>
                <select name="codCurso" value={this.state.aluno.codCurso}  onChange={e => { this.atualizaCurso(e)}}>
                    <option>
                        Selecione o Curso
                    </option>
                    {this.state.listaCursos.map(
                            (curso) =>
                            <option  key={curso.id} name="codCurso" value={curso.codCurso}>
                                { curso.codCurso } - { curso.nomeCurso } - { curso.periodo }
                            </option>
                        )}
                </select>

                <button className="btnSalvar"
                    onClick={e => this.salvar(e)} >
                        Salvar
                </button>

                <button className="btnCancelar"
                    onClick={e => this.limpar(e)} >
                        Cancelar
                </button>

            </div>
        )
    }

    atualizaCurso(event) {
        const aluno = { ...this.state.aluno };
        aluno.codCurso = Number(event.target.value);
        this.setState({ aluno })
    }

    renderTable() {
        return(
            <div className="listagem">
                <table className="listaAlunos" id="tblListaAlunos">
                    <thead>
                        <tr className="cabecTabela">
                            <th className="tabTituloRa">Ra</th>
                            <th className="tabTituloNome">Nome</th>
                            <th className="tabTituloCurso">Curso</th>
                        </tr>
                    </thead>

                    <tbody>
                        {this.state.lista.map(
                            (aluno) =>
                            <tr key={aluno.id}>
                                <td>{aluno.ra}</td>
                                <td>{aluno.nome}</td>
                                <td>{aluno.codCurso}</td>
                                <td>
                                    <button onClick={() => this.carregar(aluno)}>
                                        Altera
                                    </button>
                                </td>
                                <td>
                                    <button onClick={() => this.remover(aluno)}>
                                        Remove
                                    </button>
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        )
    }
    render() {
        return(
            <Main title={title}>
                {this.renderForm()}
                {this.renderTable()}
            </Main>
        )
    }
}
