import React, { Component } from 'react';
import axios from 'axios';
import './CrudEdtTenis.css';
import Main from '../template/Main';

const title = "Editar Coleção de Tenis";

const urlAPI = "http://localhost:5174/api/tenis";
const initialState = {
    tenis: {id: 0, cod: '', nome:'', tamanho: 0},
    lista: []
}

export default class CrudTenis extends Component {

    state = {...initialState}

    componentDidMount() {
        axios(urlAPI).then(resp => {
            this.setState({ lista: resp.data })
        })
    }

    limpar() {
        this.setState({ tenis: initialState.tenis });
    }

    salvar() {
        const metodo = tenis.cod ? 'put' : 'post';
        const url = tenis.id ? `${urlAPI}/${tenis.id}` : urlAPI;

        const tenis = this.state.tenis;
        tenis.cod = Number(tenis.cod);

        axios[metodo](url, tenis)
            .then(resp => {
                const lista = this.getListaAtualizada(resp.data)
                this.setState({ tenis: initialState.tenis, lista })
            })
    }

    getListaAtualizada(tenis, add = true) {
        const lista = this.state.lista.filter(a => a.id !== tenis.id);
        if (add) lista.unshift(tenis);
        return lista;
    }

    atualizaCampo(evento) {
        const tenis = { ...this.state.tenis};
        tenis[evento.target.name] = evento.target.value;
        this.setState({tenis});
    }

    carregar(tenis) {
        this.setState({ tenis })
        const url = urlAPI + "/" + tenis.id;
        if (window.confirm("Confirma edição do tenis: " + tenis.ra)) {
            console.log("entrou no confirm");
            axios['PUT'](url, tenis).then(resp => {
                const lista = this.getListaAtualizada(tenis, false);
                this.setState({ tenis: initialState.tenis, lista });
            });
        }
        this.atualizaCampo(tenis);
    }

    remover(tenis) {
        const url = urlAPI + "/" + tenis.id;
        if (window.confirm("Confirma remoção do tenis: " + tenis.ra)) {
            console.log("entrou no confirme da tela")

            axios['delete'](url, tenis)
                .then(resp => {
                    const lista = this.getListaAtualizada(tenis, false)
                    this.setState({ tenis: initialState.tenis, lista })
                })
        }
    }

    renderForm(){
        return(
            <div className='inclui-container'>
                <label>Codigo</label>
                <input
                    type='text'
                    id='cod'
                    placeholder='Cod do Tênis'
                    className='form-input1'
                    name='cod'
                    value={this.state.tenis.cod}
                    onChange={e => this.atualizaCampo(e)}
                />
                <label>Nome</label>
                <input
                    type='text'
                    id='nome'
                    placeholder='Nome do Tênis'
                    className='form-input2'
                    name='nome'
                    value={this.state.tenis.nome}
                    onChange={e => this.atualizaCampo(e)}
                /> 
                <label>Tamanho</label>
                <input
                    type='text'
                    id='tamanho'
                    placeholder='Tamanho do Tênis'
                    className='form-input'
                    name='tamanho'
                    value={this.state.tenis.tamanho}
                    onChange={e => this.atualizaCampo(e)}
                />
                <button className='btnSalvar'
                    onClick={e => this.salvar(e)}>
                        Salvar
                </button>
                <button className="btnCancelar"
                    onClick={e => this.limpar(e)} >
                        Cancelar
                </button>
            </div>
        )
    }


    renderTable() {
        return (
            <div className="listagem">
                <table className="listaTenis" id="tblListaTenis">
                    <thead>
                        <tr className="cabecTabela">
                            <th className="tabTituloCod">Codigo</th>
                            <th className="tabTituloNome">Nome do Sneaker</th>
                            <th className="tabTituloTamanho">Tamanho</th>
                        </tr>
                    </thead>

                    <tbody>
                        {this.state.lista.map(
                            (tenis) =>
                            <tr key={tenis.id}>
                                <td>{tenis.cod}</td>
                                <td>{tenis.nome}</td>
                                <td>{tenis.tamanho}</td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        )
    }

    render() {
        return (
            <Main title={title}>
                {this.renderForm()}
                {this.renderTable()}
            </Main>
        )
    }
}
