import React, {Component} from "react";
import axios from 'axios';
import './CrudTenis.css';
import Main from "../template/Main";

const title = "Cadastro de Tenis";

const urlAPI = 'http://localhost:5174/api/tenis';

const initialState = {
    tenis: { id: 0, Cod: '', nome: '', tamanho: 0},
    lista: []
}

export default class CrudTenis extends Component {
    state = { ...initialState}

    componentDidMount() {
        axios(urlAPI).then(resp => {
            this.setState({ lista: resp.data })
        })
    }

    limpar() {
        this.setState({ tenis: initialState.tenis });
    }

    salvar() {
        const tenis = this.state.tenis;
        tenis.Cod = Number(tenis.Cod);

        const metodo = tenis.id ? 'put' : 'post';
        const url = tenis.id ? `${urlAPI}/${tenis.id}` : urlAPI;

        axios[metodo](url, tenis)
            .then(resp => {
                const lista = this.getListaAtualizada(resp.data)
                this.setState({ tenis: initialState.tenis, lista })
            })
    }

    getListaAtualizada(tenis, add = true) {
        const lista = this.state.lista.filter(a => a.id !== tenis.id);
        if (add) lista.unshift(tenis);
        return lista;
    }

    atualizaCampo(evento) {
        const tenis = { ...this.state.tenis};
        tenis[evento.target.name] = evento.target.value;
        this.setState({tenis});
    }

    carregar(tenis) {
        this.setState({ tenis })
        const url = urlAPI + "/" + tenis.id;
        if (window.confirm("Confirma edição do tenis: " + tenis.Cod)) {
            console.log("entrou no confirm");
            axios['PUT'](url, tenis).then(resp => {
                const lista = this.getListaAtualizada(tenis, false);
                this.setState({ tenis: initialState.tenis, lista });
            });
        }
        this.atualizaCampo(tenis);
    }

    remover(tenis) {
        const url = urlAPI + "/" + tenis.id;
        if (window.confirm("Confirma remoção do tenis: " + tenis.Cod)) {
            console.log("entrou no confirme da tela")

            axios['delete'](url, tenis)
                .then(resp => {
                    const lista = this.getListaAtualizada(tenis, false)
                    this.setState({ tenis: initialState.tenis, lista })
                })
        }
    }

    renderForm() {
        return (
            <div className="inclui-container">

                <label> Cod: </label>
                <input
                    type="text"
                    id="Cod"
                    placeholder="Cod do tenis"
                    className="form-input"
                    name="Cod"
                    value={this.state.tenis.Cod}
                    onChange={ e => this.atualizaCampo(e)}
                />

                <label> Nome: </label>
                <input
                    type="text"
                    id="nome"
                    placeholder="Nome do tenis"
                    className="form-input"
                    name="nome"
                    value={this.state.tenis.nome}
                    onChange={ e => this.atualizaCampo(e)}
                />

                <label> ta: </label>
                <select name="Cod" value={this.state.tenis.Cod}  onChange={e => { this.atualizaCurso(e)}}>
                    <option>
                        Selecione o Curso
                    </option>
                    {this.state.listaCursos.map(
                            (curso) =>
                            <option  key={curso.id} name="Cod" value={curso.Cod}>
                                { curso.Cod } - { curso.nomeCurso } - { curso.periodo }
                            </option>
                        )}
                </select>

                <button className="btnSalvar"
                    onClick={e => this.salvar(e)} >
                        Salvar
                </button>

                <button className="btnCancelar"
                    onClick={e => this.limpar(e)} >
                        Cancelar
                </button>

            </div>
        )
    }

    atualizaCurso(event) {
        const tenis = { ...this.state.tenis };
        tenis.Cod = Number(event.target.value);
        this.setState({ tenis })
    }

    renderTable() {
        return(
            <div className="listagem">
                <table className="listateniss" id="tblListateniss">
                    <thead>
                        <tr className="cabecTabela">
                            <th className="tabTituloCod">Cod</th>
                            <th className="tabTituloNome">Nome</th>
                            <th className="tabTituloCurso">Curso</th>
                        </tr>
                    </thead>

                    <tbody>
                        {this.state.lista.map(
                            (tenis) =>
                            <tr key={tenis.id}>
                                <td>{tenis.Cod}</td>
                                <td>{tenis.nome}</td>
                                <td>{tenis.Cod}</td>
                                <td>
                                    <button onClick={() => this.carregar(tenis)}>
                                        AlteCod
                                    </button>
                                </td>
                                <td>
                                    <button onClick={() => this.remover(tenis)}>
                                        Remove
                                    </button>
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        )
    }
    render() {
        return(
            <Main title={title}>
                {this.renderForm()}
                {this.renderTable()}
            </Main>
        )
    }
}
