import React, {Component} from "react";
import axios from 'axios';
import './CrudTenis.css';
import Main from "../template/Main";

const title = "Cadastro de Tenis";

const urlAPI = 'http://localhost:5174/api/tenis';

const initialState = {
    Te: { id: 0, Cod: '', nome: '', tamanho: 0},
    lista: []
}

export default class CrudTenis extends Component {
    state = { ...initialState}

    componentDidMount() {
        axios(urlAPI).then(resp => {
            this.setState({ lista: resp.data })
        })
    }

    limpar() {
        this.setState({ Te: initialState.Te });
    }

    salvar() {
        const Te = this.state.Te;
        Te.codCurso = Number(Te.codCurso);

        const metodo = Te.id ? 'put' : 'post';
        const url = Te.id ? `${urlAPI}/${Te.id}` : urlAPI;

        axios[metodo](url, Te)
            .then(resp => {
                const lista = this.getListaAtualizada(resp.data)
                this.setState({ Te: initialState.Te, lista })
            })
    }

    getListaAtualizada(Te, add = true) {
        const lista = this.state.lista.filter(a => a.id !== Te.id);
        if (add) lista.unshift(Te);
        return lista;
    }

    atualizaCampo(evento) {
        const Te = { ...this.state.Te};
        Te[evento.target.name] = evento.target.value;
        this.setState({Te});
    }

    carregar(Te) {
        this.setState({ Te })
        const url = urlAPI + "/" + Te.id;
        if (window.confirm("Confirma edição do Te: " + Te.ra)) {
            console.log("entrou no confirm");
            axios['PUT'](url, Te).then(resp => {
                const lista = this.getListaAtualizada(Te, false);
                this.setState({ Te: initialState.Te, lista });
            });
        }
        this.atualizaCampo(Te);
    }

    remover(Te) {
        const url = urlAPI + "/" + Te.id;
        if (window.confirm("Confirma remoção do Te: " + Te.ra)) {
            console.log("entrou no confirme da tela")

            axios['delete'](url, Te)
                .then(resp => {
                    const lista = this.getListaAtualizada(Te, false)
                    this.setState({ Te: initialState.Te, lista })
                })
        }
    }

    renderForm() {
        return (
            <div className="inclui-container">

                <label> RA: </label>
                <input
                    type="text"
                    id="ra"
                    placeholder="RA do Te"
                    className="form-input"
                    name="ra"
                    value={this.state.Te.ra}
                    onChange={ e => this.atualizaCampo(e)}
                />

                <label> Nome: </label>
                <input
                    type="text"
                    id="nome"
                    placeholder="Nome do Te"
                    className="form-input"
                    name="nome"
                    value={this.state.Te.nome}
                    onChange={ e => this.atualizaCampo(e)}
                />

                <label> Código do Curso: </label>
                <select name="codCurso" value={this.state.Te.codCurso}  onChange={e => { this.atualizaCurso(e)}}>
                    <option>
                        Selecione o Curso
                    </option>
                    {this.state.listaCursos.map(
                            (curso) =>
                            <option  key={curso.id} name="codCurso" value={curso.codCurso}>
                                { curso.codCurso } - { curso.nomeCurso } - { curso.periodo }
                            </option>
                        )}
                </select>

                <button className="btnSalvar"
                    onClick={e => this.salvar(e)} >
                        Salvar
                </button>

                <button className="btnCancelar"
                    onClick={e => this.limpar(e)} >
                        Cancelar
                </button>

            </div>
        )
    }

    atualizaCurso(event) {
        const Te = { ...this.state.Te };
        Te.codCurso = Number(event.target.value);
        this.setState({ Te })
    }

    renderTable() {
        return(
            <div className="listagem">
                <table className="listaTes" id="tblListaTes">
                    <thead>
                        <tr className="cabecTabela">
                            <th className="tabTituloRa">Ra</th>
                            <th className="tabTituloNome">Nome</th>
                            <th className="tabTituloCurso">Curso</th>
                        </tr>
                    </thead>

                    <tbody>
                        {this.state.lista.map(
                            (Te) =>
                            <tr key={Te.id}>
                                <td>{Te.ra}</td>
                                <td>{Te.nome}</td>
                                <td>{Te.codCurso}</td>
                                <td>
                                    <button onClick={() => this.carregar(Te)}>
                                        Altera
                                    </button>
                                </td>
                                <td>
                                    <button onClick={() => this.remover(Te)}>
                                        Remove
                                    </button>
                                </td>
                            </tr>
                        )}
                    </tbody>
                </table>
            </div>
        )
    }
    render() {
        return(
            <Main title={title}>
                {this.renderForm()}
                {this.renderTable()}
            </Main>
        )
    }
}
